Page._content:
/* Company Logo Styles */
.company-link {
  display: inline-flex;
  align-items: center;
  text-decoration: none;
  color: #333;
  transition: opacity 0.3s ease;
}
.company-link:hover {
  opacity: 0.7;
}
.company-logo {
  height: 20px;
  width: auto;
  margin-right: 8px;
  vertical-align: middle;
}
.company-logo-en {
  height: 20px;
  width: auto;
  margin-right: 8px;
  vertical-align: middle;
}

/* Language Switcher */
.language-switcher {
  display: inline-block;
  margin-left: 15px;
}
.language-switcher a {
  text-decoration: none;
  padding: 5px 10px;
  border-radius: 3px;
  font-size: 14px;
}
.language-switcher a.active {
  background-color: #333;
  color: #fff;
}
.language-switcher a:not(.active) {
  background-color: #f5f5f5;
  color: #333;
}

/* Sidebar Avatar */
.sidebar-avatar img {
  width: 120px;
  height: 120px;
  border-radius: 50%;
  object-fit: cover;
}

/* Page Styles */
.page-content {
  padding: 20px;
}
.work-experience-item {
  margin-bottom: 30px;
  padding-bottom: 20px;
  border-bottom: 1px solid #eee;
}
.work-experience-item:last-child {
  border-bottom: none;
}
.company-header {
  display: flex;
  align-items: center;
  margin-bottom: 10px;
}
.company-name {
  font-size: 18px;
  font-weight: 600;
  color: #333;
}
.company-name-en {
  font-size: 18px;
  font-weight: 600;
  color: #333;
}
.job-title {
  font-size: 14px;
  color: #666;
  margin-bottom: 10px;
}
.job-period {
  font-size: 12px;
  color: #999;
  margin-bottom: 10px;
}
.job-keywords {
  font-size: 12px;
  color: #0066cc;
  margin-bottom: 15px;
}
.job-description {
  font-size: 14px;
  line-height: 1.8;
  color: #555;
}
.job-description ul {
  padding-left: 20px;
}
.job-description li {
  margin-bottom: 8px;
}

